	public static function sendEmail(array $recepients, string $subject, string $message, string $textMessage, array $email=self::NO_REPLY_EMAIL, array $smtp=self::NO_REPLY_SMTP, string $pass=self::NO_REPLY_PASSWORD) {
		$mail = new \PHPMailer\PHPMailer\PHPMailer(false);
		$mail->SMTPDebug = 0;
		$mail->isSMTP();
		$mail->Host = $smtp[0];
		$mail->SMTPAuth = true;
		$mail->Username = $email[0];
		$mail->Password = $pass;
		$mail->SMTPSecure = $smtp[2];
		$mail->Port = $smtp[1];

		$mail->setFrom(...$email);
		foreach ($recepients as $to) {
			$mail->addAddress(...$to);
		}

		$mail->isHTML(true);
		$mail->Subject = $subject;
		$mail->Body = $message;
		$mail->AltBody = $textMessage;

		$mail->send();
	}
}
